import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../../core/services/auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  name = '';
  email = '';
  password = '';
  confirmPassword = '';
  errorMessage = '';
  isLoading = false;

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  register(): void {
    if (!this.name || !this.email || !this.password) {
      this.errorMessage = 'All fields are required.';
      return;
    }

    if (this.password !== this.confirmPassword) {
      this.errorMessage = 'Passwords do not match.';
      return;
    }

    this.isLoading = true;
    this.errorMessage = '';

    this.authService.register({
      name: this.name,
      email: this.email,
      password: this.password
    }).subscribe({
      next: () => {
        this.isLoading = false;
        // After successful registration → go to login
        this.router.navigate(['/login']);
      },
      error: (err) => {
        this.isLoading = false;
        this.errorMessage =
          err?.error?.message || 'Registration failed.';
      }
    });
  }
}
<div class="min-h-screen flex items-center justify-center bg-gray-100">

  <div class="bg-white p-6 rounded shadow w-full max-w-sm">

    <h2 class="text-2xl font-bold mb-6 text-center">
      Register
    </h2>

    <!-- Error -->
    <p *ngIf="errorMessage" class="text-red-600 mb-3 text-sm">
      {{ errorMessage }}
    </p>

    <!-- Name -->
    <input
      type="text"
      placeholder="Full Name"
      [(ngModel)]="name"
      class="border rounded px-3 py-2 w-full mb-3"
    />

    <!-- Email -->
    <input
      type="email"
      placeholder="Email"
      [(ngModel)]="email"
      class="border rounded px-3 py-2 w-full mb-3"
    />

    <!-- Password -->
    <input
      type="password"
      placeholder="Password"
      [(ngModel)]="password"
      class="border rounded px-3 py-2 w-full mb-3"
    />

    <!-- Confirm Password -->
    <input
      type="password"
      placeholder="Confirm Password"
      [(ngModel)]="confirmPassword"
      class="border rounded px-3 py-2 w-full mb-4"
    />

    <!-- Loader -->
    <app-loader *ngIf="isLoading" text="Creating account..."></app-loader>

    <!-- Submit -->
    <button
      (click)="register()"
      [disabled]="isLoading"
      class="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700 disabled:opacity-50"
    >
      Register
    </button>

    <!-- Login link -->
    <p class="text-sm text-center text-gray-600 mt-4">
      Already have an account?
      <a routerLink="/login" class="text-blue-600 hover:underline">
        Login
      </a>
    </p>

  </div>
</div>
