import { Component } from '@angular/core';
import { DocumentService } from '../../core/services/document.service';


@Component({
  selector: 'app-document-upload',
  templateUrl: './document-upload.component.html',
  styleUrls: ['./document-upload.component.css']
})
export class DocumentUploadComponent {

  title = '';
  description = '';
  tags = '';
  accessLevel = 'PRIVATE';
  file: File | null = null;

  isUploading = false;
  errorMessage = '';

  constructor(private documentService: DocumentService) {}

  onFileChange(event: Event): void {
    const input = event.target as HTMLInputElement;
    if (input.files && input.files.length > 0) {
      this.file = input.files[0];
    }
  }

  upload(): void {
    if (!this.file || !this.title) {
      this.errorMessage = 'Title and file are required';
      return;
    }

    const formData = new FormData();
    formData.append('file', this.file);
    formData.append('title', this.title);
    formData.append('description', this.description);
    formData.append('tags', this.tags);
    formData.append('accessLevel', this.accessLevel);

    console.log('Uploading document...', formData);

    this.isUploading = true;

    this.documentService.uploadDocument(formData).subscribe({
      next: () => {
        this.isUploading = false;
        this.router.navigate(['/dashboard']);
      },
      error: (err) => {
        console.error(err);
        this.isUploading = false;
        this.errorMessage = 'Upload failed';
      }
    });
  }

}
<div class="p-6 max-w-xl mx-auto">

  <h2 class="text-2xl font-bold mb-6">Upload Document</h2>

  <!-- Error -->
  <p *ngIf="errorMessage" class="text-red-600 mb-3">
    {{ errorMessage }}
  </p>

  <!-- Title -->
  <input
    type="text"
    placeholder="Document Title"
    [(ngModel)]="title"
    class="border rounded px-3 py-2 w-full mb-3"
  />

  <!-- Description -->
  <textarea
    placeholder="Description"
    [(ngModel)]="description"
    class="border rounded px-3 py-2 w-full mb-3"
  ></textarea>

  <!-- Tags -->
  <input
    type="text"
    placeholder="Tags (comma separated)"
    [(ngModel)]="tags"
    class="border rounded px-3 py-2 w-full mb-3"
  />

  <!-- Access Level -->
  <select
    [(ngModel)]="accessLevel"
    class="border rounded px-3 py-2 w-full mb-3"
  >
    <option value="PRIVATE">Private</option>
    <option value="PUBLIC_VIEW">Public View</option>
  </select>

  <!-- File -->
  <input
    type="file"
    (change)="onFileChange($event)"
    class="mb-4"
  />

  <!-- Loader -->
  <app-loader *ngIf="isUploading" text="Uploading..."></app-loader>

  <!-- Submit -->
  <button
    (click)="upload()"
    [disabled]="isUploading"
    class="bg-blue-600 text-white px-4 py-2 rounded"
  >
    Upload
  </button>

</div>
