import { Injectable } from '@angular/core';
import {
  CanActivate,
  ActivatedRouteSnapshot,
  RouterStateSnapshot,
  Router
} from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class AuthGuard implements CanActivate {

  constructor(private router: Router) {}

  canActivate(
    route: ActivatedRouteSnapshot,
    state: RouterStateSnapshot
  ): boolean {

    const token = localStorage.getItem('token');
    const user = localStorage.getItem('user');

    // If token exists and is valid → allow access
    if (token && token !== 'undefined' && token !== null && user && user !== 'undefined') {
      console.log('Auth Guard: Token valid, allowing access');
      return true;
    }

    // If not logged in → clear storage and redirect to login
    console.warn('Auth Guard: No valid token found, redirecting to login');
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    this.router.navigate(['/auth/login'], {
      queryParams: { returnUrl: state.url }
    });

    return false;
  }
}
