import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DocumentService } from '../../core/services/document.service';

@Component({
  selector: 'app-document-view',
  templateUrl: './document-view.component.html',
  styleUrls: ['./document-view.component.css']
})
export class DocumentViewComponent implements OnInit {

  document: any;
  isLoading = false;
  canEdit = false;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private documentService: DocumentService
  ) {}

  ngOnInit(): void {
    const documentId = this.route.snapshot.paramMap.get('id');
    if (documentId) {
      this.loadDocument(documentId);
    }
  }

  loadDocument(documentId: string): void {
    this.isLoading = true;

    this.documentService.getDocumentById(documentId).subscribe({
      next: (res) => {
        this.document = res;
        this.canEdit = this.checkEditPermission(res);
        this.isLoading = false;
      },
      error: () => {
        this.isLoading = false;
        this.router.navigate(['/dashboard']);
      }
    });
  }

  checkEditPermission(doc: any): boolean {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    return (
      doc.uploadedBy?._id === user._id ||
      doc.permissions?.some(
        (p: any) => p.userId === user._id && p.canEdit
      )
    );
  }
}
<div class="p-6">

  <!-- Loader -->
  <app-loader *ngIf="isLoading"></app-loader>

  <ng-container *ngIf="!isLoading && document">

    <h2 class="text-2xl font-bold mb-2">
      {{ document.title }}
    </h2>

    <p class="text-gray-600 mb-4">
      {{ document.description || 'No description provided.' }}
    </p>

    <!-- Meta -->
    <div class="flex flex-wrap gap-4 text-sm text-gray-500 mb-4">
      
    </div>

    <!-- Tags -->
    <div class="flex flex-wrap gap-2 mb-6">
      <span
        *ngFor="let tag of document.tags"
        class="bg-blue-100 text-blue-700 text-xs px-2 py-1 rounded"
      >
        {{ tag }}
      </span>
    </div>

    <!-- Actions -->
    <div class="flex flex-wrap gap-3">

      <a
        [href]="document.fileUrl"
        target="_blank"
        class="px-4 py-2 bg-gray-200 rounded"
      >
        View File
      </a>

      <button
        *ngIf="canEdit"
        [routerLink]="['/dashboard/edit', document._id]"
        class="px-4 py-2 bg-blue-600 text-white rounded"
      >
        Edit Document
      </button>

      <button
        [routerLink]="['/dashboard/versions', document._id]"
        class="px-4 py-2 bg-gray-600 text-white rounded"
      >
        Version History
      </button>

    </div>


  </ng-container>
</div>
